import "./main.css";
import React, { useState, useEffect, useRef, forwardRef, ReactNode } from "react";
import ReactDOM from "react-dom";
// import styled from "@emotion/styled";
import styled, { keyframes } from "styled-components";
import { useHorizontalScroll } from "./useSideScroll.tsx";


const App = () => {
  const scrollRef = useHorizontalScroll();

  const scrollToPosition = (positionX: number) => {
    if (scrollRef.current) {
      scrollRef.current.scrollTo({
        left: positionX,
        behavior: "smooth",
      });
    }
  };

  return (
    <>
      <NavigationBar f={scrollToPosition} />
      <div className="App" ref={scrollRef} style={{ overflow: "auto" }}>
        <div style={{ whiteSpace: "nowrap" }}>
          <StoryBox f={scrollToPosition}></StoryBox>
        </div>
      </div>
      <ShowHelp/>
    </>
  );
};

const CommentBoxRoot = styled.div`
  display: flex;
  flex-direction: column;
  width: 42%;
  height: 50%;
  position: fixed;
  z-index: 100;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: #1C1C1C;
  // gap: 24px;
  box-shadow: 0px 0px 20px 5px rgba(201,218,93,.5);
`;

const CommentHeader = styled.div`
  display: flex;
  margin: 48px 48px 24px 48px;
  justify-content: space-between;
  height: 40px;
  color: #C9DA5D;
  font-size: 2.5vh;
`;

const CommentContent = styled.textarea`
  display: flex;
  margin-left: 48px;
  margin-right: 48px;
  color: #53592D;
  width: auto;
  height: 65%;
  background: #1C1C1C;
  font-size: 2.5vh;
  resize: none;
  outline: none;
  line-height: 1.7;
`;
const CommentSend = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 48px 48px 48px 48px;
  color: #C9DA5D;
  font-size: 2.5vh;
`;

interface CommentBoxProps {
  name: string;
  comments: string;
  setComment: (x: string) => void;
  setInputPhase: (x: boolean) => void;
}

const CommentBox = ({
  name,
  comments,
  setComment,
  setInputPhase,
}: CommentBoxProps) => {
  const [content, setContent] = useState<string>(
    "이곳에 전하고 싶은 이야기를 작성해주세요. 인물의 이름을 클릭하면 기록된 이야기를 볼 수 있어요."
  );
  const [errormessage, setErrorMessage] = useState<string>("");

  const SendComment = () => {
    if (
      content === "이곳에 전하고 싶은 이야기를 작성해주세요. 인물의 이름을 클릭하면 기록된 이야기를 볼 수 있어요." ||
      content === ""
    ) {
      setErrorMessage("이야기를 입력해주세요.");
    } else {
      setComment(content + "\n" + comments);
      setInputPhase(false);
    }
  };

  return (
    <CommentBoxRoot>
      <CommentHeader>
        <div
          style={{ textDecoration: "underline dotted 3px", textUnderlineOffset: 
        "12px" }}
        >{`'${name}'에게 전할 이야기가 있나요?`}</div>
        <div style={{ cursor: "pointer" }} onClick={() => setInputPhase(false)}>
          닫기
        </div>
      </CommentHeader>
      <CommentContent
        value={content}
        onChange={(e) => setContent(e.target.value)}
        onFocus={() => {
          setErrorMessage("");
          if (content === "이곳에 전하고 싶은 이야기를 작성해주세요. 인물의 이름을 클릭하면 기록된 이야기를 볼 수 있어요.") {
            setContent("");
          }
        }}
        style={{
          color: content === "이곳에 전하고 싶은 이야기를 작성해주세요. 인물의 이름을 클릭하면 기록된 이야기를 볼 수 있어요." || content === "" ? '#53592D' : '#C9DA5D'
        }}
      ></CommentContent>
      <CommentSend>
        <div>{errormessage}</div>
        <div style={{ cursor: "pointer" }} onClick={SendComment}>
          보내기
        </div>
      </CommentSend>
    </CommentBoxRoot>
  );
};



