import React, { useRef, useState, useEffect } from "react";
import ReactDOM from "react-dom";
import styled from "@emotion/styled";
import { useHorizontalScroll } from "./useSideScroll.tsx";

const App = () => {
  const scrollRef = useHorizontalScroll();

  const scrollToPosition = (positionX : number) => {
    if(scrollRef.current) {
      scrollRef.current.scrollTo({
        left: positionX,
        behavior: 'smooth'
      })
    }
  }

  return (
    <>
    <NavigationBar f={scrollToPosition}></NavigationBar>
    <div className="App" ref={scrollRef} style={{ overflow: "auto" }}>
      <div style={{ whiteSpace: "nowrap" }}>
        <StoryBox></StoryBox>
      </div>
    </div></>
  );

}

const StoryBoxRoot = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  height: 50vw;
  width: 500vw;
  gap: 240px;
  padding-left: 36px;

  background: #EED555;
`

const Box = styled.div`
  width: 480px;
  border: 1px solid black;
`

const Story = styled.div`
  display: flex;
  overflow: hidden;
  // word-wrap: break-word;
  margin-top: 240px;
  margin-left: 12px;
  width: 480px;
  height: 200px;
  font-size: 20px;
  border: 1px solid black;
`

const StoryBox = () => {

  return (
    <StoryBoxRoot>

    <Box>
      <Story>양규의 아들, 한석이 고종 병술년 11월 28일에 태어난다. 이후 한석은 예규에게 입양된다. 한석은 보성 선씨와 결혼한다. 배우자인 선씨는 신묘년 2월 15일에 태어났으며, 아버지의 성함은 선영문이다. 한석은 1959년 5월 4일에 생을 마감하고, 가장등 가족묘지에 잠들어있다. 선씨는 1958년 12월 20일에 생을 마감하고, 우동간 합봉유비에 잠들어있다.
      </Story>
    </Box>
    <Box>
      <Story>
        안녕
      </Story>
    </Box>

    </StoryBoxRoot>
  )
}


const NavigationBarRoot = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  height : 112px;
  width : 90vw;
  position: fixed;
  top: 0;
  z-index: 100;
  margin: 48px;
`

const NavigationBarItem = styled.div`
  display: flex;
  align-items: center;
  font-size: 32px;
`
interface NavigationBarProps {
  f : (positionX : number) => void
}
const NavigationBar = ({f} : NavigationBarProps) => {
  return (
    <NavigationBarRoot>
      <NavigationBarItem onClick={() => f(0)}>[청주 김씨]</NavigationBarItem>
      <NavigationBarItem onClick={() => f(1000)}>15대</NavigationBarItem>
      <NavigationBarItem onClick={() => f(2000)}>16대</NavigationBarItem>
      <NavigationBarItem onClick={() => f(3000)}>17대</NavigationBarItem>
      <NavigationBarItem onClick={() => f(4000)}>18대</NavigationBarItem>
      <NavigationBarItem onClick={() => f(5000)}>19대</NavigationBarItem>
    </NavigationBarRoot>
  )
}


ReactDOM.render(
  <App/>
 ,
  document.getElementById("root")
);
