import { useRef, useEffect } from "react";

export function useScrollTo( ): React.RefObject<HTMLDivElement> {
  const elRef = useRef<HTMLDivElement>(null);
  useEffect(() => {
    const el = elRef.current;
    if (el) {
      el.scrollTo({
        left: 300,

      });

    }
  }, []);
  return elRef;
}
