import "./main.css";
import React, { useRef, useState, useEffect } from "react";
import ReactDOM from "react-dom";
import styled from "@emotion/styled";
import { useHorizontalScroll } from "./useSideScroll.tsx";

const App = () => {
  const scrollRef = useHorizontalScroll();

  const scrollToPosition = (positionX: number) => {
    if (scrollRef.current) {
      scrollRef.current.scrollTo({
        left: positionX,
        behavior: "smooth",
      });
    }
  };

  return (
    <>
      <NavigationBar f={scrollToPosition} />
      <div className="App" ref={scrollRef} style={{ overflow: "auto" }}>
        <div style={{ whiteSpace: "nowrap" }}>
          <StoryBox f={scrollToPosition}></StoryBox>
        </div>
      </div>
      <ShowHelp />
    </>
  );
};

const CommentBoxRoot = styled.div`
  display: flex;
  flex-direction: column;
  width: 601px;
  height: 408px;
  position: fixed;
  z-index: 100;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: #000;
  gap: 24px;
`;

const CommentHeader = styled.div`
  display: flex;
  margin: 24px 24px 0px 24px;
  justify-content: space-between;
  height: 40px;
  color: #eed555;
  font-size: 20px;
`;

const CommentContent = styled.textarea`
  display: flex;
  margin-left: 48px;
  margin-right: 48px;
  color: #eed555;
  width: 505px;
  height: 232px;
  background: #000;
  border: 1px solid black;
  font-size: 20px;
  resize: none;
  outline: none;
`;
const CommentSend = styled.div`
  display: flex;
  justify-content: space-between;
  margin-left: 24px;
  margin-right: 24px;
  color: #eed555;
  font-size: 20px;
`;

interface CommentBoxProps {
  name: string;
  comments: string;
  setComments: (x: string) => void;
  setInputPhase: (x: boolean) => void;
}

const CommentBox = ({
  name,
  comments,
  setComments,
  setInputPhase,
}: CommentBoxProps) => {
  const [content, setContent] = useState<string>(
    "이곳에 전하고 싶은 이야기를 작성해주세요."
  );
  const [errormessage, setErrorMessage] = useState<string>("");

  const SendComment = () => {
    if (
      content === "이곳에 전하고 싶은 이야기를 작성해주세요." ||
      content === ""
    ) {
      setErrorMessage("이야기를 입력해주세요.");
    } else {
      setComments(comments + content + "\n");
      setInputPhase(false);
    }
  };

  return (
    <CommentBoxRoot>
      <CommentHeader>
        <div
          style={{ textDecoration: "underline" }}
        >{`'${name}'에게 전할 이야기가 있나요?`}</div>
        <div style={{ cursor: "pointer" }} onClick={() => setInputPhase(false)}>
          X
        </div>
      </CommentHeader>
      <CommentContent
        value={content}
        onChange={(e) => setContent(e.target.value)}
        onFocus={() => {
          setErrorMessage("");
          if (content === "이곳에 전하고 싶은 이야기를 작성해주세요.") {
            setContent("");
          }
        }}
      ></CommentContent>
      <CommentSend>
        <div>{errormessage}</div>
        <div style={{ cursor: "pointer" }} onClick={SendComment}>
          보내기
        </div>
      </CommentSend>
    </CommentBoxRoot>
  );
};

const StoryBoxRoot = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  height: 100vh;
  width: 1050vw;
  gap: 17.7vw;
  padding-left: 48px;

  background: #eed555;
`;
const GenRoot = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-bottom: 51px;
`;

const Box = styled.div`
  display: flex;
  flex-direction: row;
  gap: 1.6vw;
`;

const Column = styled.div`
  position: relative;
`;

const Story = styled.div`
  display: flex;
  word-wrap: break-word;
  white-space: pre-wrap;

  margin-top: 176px;
  width: 30.6vw;
  height: 200px;
  font-size: 2.5vh;
  line-height: 1.7;
`;

